package com.eethan.ineedu.model;

public class ContactModel {
	private int id;
	private String name;
	private String sex;
	
	public ContactModel(int id,String name,String sex) {
		// TODO Auto-generated constructor stub
		this.id = id;
		this.name = name;
		this.sex = sex;
	}
	public ContactModel(){}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getSex() {
		return sex;
	}
	public void setSex(String sex) {
		this.sex = sex;
	}
	
}
